class TrieNode {
    constructor() {
        this.children = new Array(26)
        this.count = 0
        this.children.fill(null)
        this.isend = false
    }
}

class Trie {
    constructor() {
        this.root = new TrieNode()
    }
    
    insert(word) {
        let curr = this.root
        for(let ch of word) {
            let index = ch.charCodeAt() - 'a'.charCodeAt()
            if(curr[index] == null) {
                curr[index] = new TrieNode()
            }
            curr[index].count++
            curr = curr[index]
        }
        curr.isend = true
    }
    
    search(prefix) {
        let curr = this.root
        for(let ch of prefix) {
            let index = ch.charCodeAt() - 'a'.charCodeAt()
            if(curr[index] == null) {
                return 0
            }
            curr = curr[index]
        }
        return curr.count
    }
    optlength(prefix,thresh) {
        let curr = this.root
        let indx=1;
        for(let ch of prefix) {
            let index = ch.charCodeAt() - 'a'.charCodeAt()
            if(curr[index] == null) {
                return 0
            }
            curr = curr[index]
            if(curr.count<thresh) return indx
            indx++;
        }
        return indx;
    }
 }
let prefixCount = function(words, pref) {
    let trie = new Trie()
    for(let word of words) {
        trie.insert(word)
    }
    return trie.search(pref)
};

